INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (3, null, '重要な来客用の会議室を予約したいのですが、会議室予約システムへのアクセス方法と、外部のお客様が利用する際の特別な注意事項はありますか？', '2025-11-15 09:00:00', 0);
INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (3, null, '部署で使うボールペンやファイルなどの文房具・事務用品は、どこに在庫があり、在庫がない場合はどのようにまとめて発注手続きをすれば良いですか？', '2025-11-15 10:00:00', 0);
INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (3, null, '社員食堂の今週のメニューや栄養情報を、出社前に事前に確認できる社内ポータルサイトや専用のアプリケーションはありますか？', '2025-11-15 11:00:00', 0);
INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (3, null, '国内出張の際の宿泊費の上限額と、タクシー利用や飲食代など、領収書の精算ルールについて、経費精算規定で確認すべき事項を教えてください。', '2025-11-15 12:00:00', 0);
INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (3, null, '部内での歓送迎会の費用を、会社が定める福利厚生費または交際費として申請できますか？その際の、申請手続きと上限金額について知りたいです。', '2025-11-15 13:00:00', 0);
INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (4, null, '母国で家族の急な病気があり、長期で帰国する必要があります。外国人社員が利用できる、特別な配慮や休暇制度、または柔軟な対応はありますか？', '2025-11-15 14:00:00', 0);
