INSERT INTO question (userId, id, question, date, deleteFlg) VALUES (4, null, '毎月、家族へ仕送りをしなければなりません。給与の一部を、日本の銀行を経由せず、直接海外の銀行に振り込んでもらう手続きは可能でしょうか？', '2025-11-15 16:00:00', 0);
