INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 14, null, 'システムログイン画面に「パスワードを忘れた方」のリンクがなければ、ITヘルプデスク（内線xxxx）に連絡してください。', '2025-11-15 17:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 14, null, 'リセットの際は、社員証と社員番号が必要です。本人確認のため、電話での連絡が最も早いですよ。', '2025-11-15 17:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 14, null, '私もパスワードをよく忘れます。パスワード管理ソフト、会社ので使えますか？', '2025-11-15 17:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 14, null, 'パスワードとか何回変えさせるの？全部「password123」とかでいいでしょ。', '2025-11-15 17:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 15, null, '通信費補助は月額3,000円です。申請は毎月の給与精算時に、専用のチェックボックスにチェックを入れるだけで大丈夫です。', '2025-11-15 18:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 15, null, '金額は一律支給です。申請は、勤怠システム内の「在宅勤務手当申請」の項目から行います。', '2025-11-15 18:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 15, null, 'この金額、インターネット代にとても助かる！情報ありがとう。', '2025-11-15 18:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 15, null, 'たった3,000円で私のギガ代が賄えるわけないじゃん。もっと出せよ。', '2025-11-15 18:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 16, null, '会議室予約システムは、社内ポータルの上部にある「設備予約」タブからアクセスできます。来客用の部屋は特に利用時間厳守です。', '2025-11-16 09:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 16, null, 'URLは`https://internal.intecs.com/room_res/`です。来客は受付での手続きが必要なので、必ず事前に総務部に通知してください。', '2025-11-16 09:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 16, null, '会議室はいつも忙しいです！時間のルール、教えてくれてありがとう。', '2025-11-16 09:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 16, null, '来客にコーヒー出すのとか、面倒くさくてダルい。どうせ男の営業の付き合いでしょ。', '2025-11-16 09:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 17, null, '在庫がない場合は、総務部に設置されている「備品発注票」に記入し、部署の上長の承認を得て提出すれば発注してもらえます。', '2025-11-16 10:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 17, null, '文房具は倉庫にあるので、まずは倉庫の在庫リストをポータルで確認してから、足りない分だけ申請しましょう。', '2025-11-16 10:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 17, null, '私はいつもペンをなくします。ひと月に何本まで頼めますか？', '2025-11-16 10:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 17, null, 'かわいい文房具がいいのに、全部地味なやつ。もっとオシャレなペンを発注してほしい。', '2025-11-16 10:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 18, null, '社員食堂のメニューは、毎朝9時までにイントラネットのトップページにある「本日の献立」セクションで更新されています。', '2025-11-16 11:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 18, null, '専用のアプリはありませんが、PCまたはスマホからイントラネットにアクセスすれば確認できますよ。', '2025-11-16 11:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 18, null, 'カレーとラーメンがあるか、いつも知りたい！良かった。トップページをチェックします。', '2025-11-16 11:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 18, null, '社員食堂とかカロリー高そう。私は毎日コンビニでサラダチキンでいいや。', '2025-11-16 11:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 19, null, '宿泊費の上限は役職によって異なります。経費精算規定を読んで、領収書は全て原本を保管してください。', '2025-11-16 12:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 19, null, '宿泊費の上限は一般社員で1泊1万円です。経費規程のPDFは「経理・財務」のフォルダにあります。', '2025-11-16 12:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 19, null, 'ホテルの上限、知って良かった！会議場所が遠い時、タクシーを使ってもいいですか？', '2025-11-16 12:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 19, null, '1万円じゃいいホテルに泊まれないじゃん。出張行くなら高級ホテルに泊まらせるべき。', '2025-11-16 12:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 20, null, '歓送迎会は福利厚生費の対象です。一人あたりの上限金額が設定されているので、事前に総務部門に確認してください。', '2025-11-16 13:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 20, null, '一人あたり5,000円が上限で、申請には参加者のリストと領収書が必要です。', '2025-11-16 13:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 20, null, '歓迎会、とても良いです！皆で一緒に楽しめたら嬉しいです。', '2025-11-16 13:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 20, null, 'どうせ男だけ飲みたがるんでしょ？女子会にもっと補助金出してほしい。', '2025-11-16 13:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 21, null, '会社の有給休暇とは別に、私傷病休暇や特別休暇の規定があります。人事総務部門に個別に相談してみてください。', '2025-11-16 14:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 21, null, 'まずは上長に事情を説明し、休暇の期間と理由を具体的に記載した申請書を提出するのが手順です。', '2025-11-16 14:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 21, null, '家族の緊急事態の場合は、会社も柔軟に対応してくれるはずです。人事総務部の窓口で直接相談してください。', '2025-11-16 14:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 21, null, '急な家族の事情とかで休めるのずるい！私も海外旅行のために使いたい。', '2025-11-16 14:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 21, null, '家族の病気、とても心配です。体を大切にしてください。', '2025-11-16 14:04:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 22, null, '会社としては正式な通訳サービスはありませんが、OJT制度で先輩社員にサポートを依頼できます。', '2025-11-16 15:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 22, null, '日本語学習については、資格取得補助制度で外部の語学学校の費用補助を受けられるか、人事部に確認できます。', '2025-11-16 15:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 22, null, '分からないことは、遠慮なく周囲の同僚に聞いてください。みんな快く助けてくれますよ。', '2025-11-16 15:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 22, null, '日本語が苦手なら、翻訳アプリでも使えばいいじゃん。仕事なんだから自分でどうにかしてよ。', '2025-11-16 15:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 22, null, '日本語の勉強、私も大変です。補助があると嬉しいです。', '2025-11-16 15:04:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 23, null, '給与の全額または一部を海外の銀行に直接振り込むサービスはありません。ご自身で日本の銀行から送金手続きが必要です。', '2025-11-16 16:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 23, null, '経理部の規定では、給与振り込みは原則として国内の銀行口座のみと定められています。', '2025-11-16 16:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 23, null, '送金手数料を節約できるネット銀行などを利用して、ご自身で送金することをおすすめします。', '2025-11-16 16:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 23, null, '仕送りとか大変だね。海外の人は家族の面倒も見なきゃいけないから、大変な人生だよね。', '2025-11-16 16:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 23, null, '給料、海外に直接は無理ですか... 残念です。', '2025-11-16 16:04:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 24, null, 'まずは上長がチェックしますが、部署によっては部署内のベテラン社員がレビューする体制があります。ご自身の部署に確認してください。', '2025-11-16 17:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 24, null, '社内には報告書テンプレートがあります。これを使えば、最低限のフォーマットは守れるはずです。', '2025-11-16 17:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 24, null, '私も日本語ネイティブですが、報告書は大変です。少しずつ慣れていきましょう！', '2025-11-16 17:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 24, null, 'なんでそんな真面目に報告書書くの？適当に箇条書きで十分だよ。', '2025-11-16 17:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 24, null, '報告書のチェック、助けが欲しいです。誰ができるか、教えてください。', '2025-11-16 17:04:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 25, null, '人事部には、外国人社員向けの生活相談窓口があります。日本の役所手続きや文化について相談できますよ。', '2025-11-16 18:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 25, null, '入社時のオリエンテーション資料に、外国人向けの緊急連絡先リストが掲載されています。', '2025-11-16 18:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 25, null, '私も日本の生活について聞かれたら教えるので、気軽に話しかけてくださいね！', '2025-11-16 18:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (5, 25, null, '日本に来るなら、自分で全部調べてくるのが常識でしょ？会社に頼りすぎじゃない？', '2025-11-16 18:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 25, null, '日本の生活、慣れるのが大変です。サポートがあるのは心強いです。', '2025-11-16 18:04:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 26, null, '体調不良などで休暇が必要な場合は、有給休暇を取得してください。私的な事情を理由に、ズル休みを推奨するような行為は会社として認められません。', '2025-11-17 09:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 26, null, '有給休暇は労働者の権利ですが、その理由を人事部に開示する義務はありません。上長に「私用」として申請してください。', '2025-11-17 09:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 26, null, '休暇の申請は、正直に「私用」で大丈夫です。人事に個人的な理由を聞かれることはありません。', '2025-11-17 09:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 26, null, '休むこと、必要な時あります。無理しないでください。', '2025-11-17 09:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 27, null, '定時退社は推奨されていますが、業務が残っている場合は残業が必要です。性別に関係なく、業務量で判断されます。', '2025-11-17 10:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 27, null, '残業は原則、上長の事前承認が必要です。承認なしでの残業を拒否する権利はあります。', '2025-11-17 10:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 27, null, '私も定時で帰る努力をしています。業務効率を上げるための相談は上司にしてみてはどうでしょうか。', '2025-11-17 10:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 27, null, '長い時間の仕事、皆に大変です。早く終わるように頑張りましょう。', '2025-11-17 10:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 28, null, '社内規定にある服装・身だしなみ規定は、会社の信用維持のためにあります。規定の改定要望は人事部へ提出できます。', '2025-11-17 11:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 28, null, 'この規定は全社員に適用されます。お客様に不快感を与えないためのビジネスマナーとして設定されています。', '2025-11-17 11:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 28, null, '私もネイルが好きですが、規定の範囲内で楽しむようにしています。個性と仕事のバランスは大事ですよ。', '2025-11-17 11:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 28, null, '日本はルールが厳しいです。私の国では、派手なネイルも大丈夫です。', '2025-11-17 11:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 29, null, '異動は、個人のスキルやキャリア、および部署の業務ニーズに基づいて判断されます。私的な目的での異動は難しいです。', '2025-11-17 12:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 29, null, 'キャリアシートには、異動希望理由を業務に関する内容で具体的に記述する必要があります。', '2025-11-17 12:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 29, null, '異動は真剣に仕事内容を考えて申請すべきですよ。個人的な理由は、もちろん書かない方が良いです。', '2025-11-17 12:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 29, null, 'パートナーを探すのはいいですが、社内のクラブで会うのはどうですか？', '2025-11-17 12:03:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (1, 30, null, 'お茶くみを含む庶務は、部署全体で協力して行うべき業務です。特定の性別に割り当てるルールはありません。', '2025-11-17 13:00:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (2, 30, null, '当番制導入の提案は、総務部に業務改善提案として提出できます。公平な分担を議論すべきです。', '2025-11-17 13:01:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (3, 30, null, '私の部署では、お茶くみは当番制になっていますよ。公平に分担できるよう、上長に提案してみては？', '2025-11-17 13:02:00', 0);
INSERT INTO answer (userId, questionId, id, answer, date, deleteFlg) VALUES (4, 30, null, 'お茶出しは、皆で分担するのがいいです。チームワークです。', '2025-11-17 13:03:00', 0);